import React, { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  // State to store the list of users
  const [users, setUsers] = useState([]);

  // useEffect hook to fetch users when the component loads
  useEffect(() => {
    // We are making a GET request to our Flask API's /users endpoint
    axios.get('http://localhost:8080/users')
      .then(response => {
        // When the data comes back, we update our users state
        setUsers(response.data);
      })
      .catch(error => {
        // It's good practice to handle potential errors
        console.error('Error fetching users:', error);
      });
  }, []); // The empty array [] means this effect runs only once

  return (
    <div className="App">
      <header className="App-header">
        <h1>User Management</h1>
      </header>
      <div className="user-list">
        <h2>All Users</h2>
        <ul>
          {/* We map over the users array to create a list item for each user */}
          {users.map(user => (
            <li key={user.id}>
              {user.username} - ({user.email})
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
