import React, { useState, useEffect } from 'react';
import axios from 'axios';
import UserForm from './UserForm'; // Import the new form

function App() {
  // State to store the list of users
  const [users, setUsers] = useState([]);

  // Function to fetch users
  const fetchUsers = () => {
    axios.get('http://localhost:8080/users')
      .then(response => {
        setUsers(response.data);
      })
      .catch(error => {
        console.error('Error fetching users:', error);
      });
  };

  // useEffect hook to fetch users when the component loads
  useEffect(() => {
    fetchUsers();
  }, []); // The empty array [] means this effect runs only once

  return (
    <div className="App">
      <header className="App-header">
        <h1>User Management</h1>
      </header>

      {/* Render the form for creating new users */}
      <UserForm />

      <div className="user-list">
        <h2>All Users</h2>
        <ul>
          {/* We map over the users array to create a list item for each user */}
          {users.map(user => (
            <li key={user.id}>
              {user.username} - ({user.email})
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
