import React, { useState, useEffect } from 'react';
import axios from 'axios';
import UserForm from './UserForm';

function App() {
  const [users, setUsers] = useState([]);

  // Moved the axios call into its own function
  const fetchUsers = () => {
    axios.get('http://localhost:8080/users')
      .then(response => {
        setUsers(response.data);
      })
      .catch(error => {
        console.error('Error fetching users:', error);
      });
  };

  // Fetch users when the component first loads
  useEffect(() => {
    fetchUsers();
  }, []);

  // This function will be passed to the UserForm
  const handleUserCreated = () => {
    // When a user is created, we re-fetch the entire list
    fetchUsers();
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>User Management</h1>
      </header>

      {/* Pass the handler function as a prop to UserForm */}
      <UserForm onUserCreated={handleUserCreated} />

      <div className="user-list">
        <h2>All Users</h2>
        <ul>
          {users.map(user => (
            <li key={user.id}>
              {user.username} - ({user.email})
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
